package day08_2ndreview;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// compound assignment operator 
		int y =1;
		y +=2; // y = y + 2;
		System.out.println("y= " + y);
		
		int i=10;
		i=i-4; //6
		i-=4; //2
		System.out.println("i = " +i);
	}

}
