package day10_doWhileLoopAndForLoop;

public class doWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i =0;
do {
	System.out.println(i);
	i++;
} while (i<5);
System.out.println("value of i is " +i);
System.out.println("---------");

i=10;
do {
	System.out.println(i);
	i--;
}while (i>=10);

}

}


